import {useEffect, useState} from "react";

export const Text = () =>{

    const [textU,setTextU] = useState(false);
    const [textI,setTextI] = useState("");

    const ocultarTexto = () =>{
        setTextU(!textU);
    }

    const handleText = (e) => {
        setTextI(e.target.value);
    }


    return <div>
        <button onClick={ocultarTexto} >{textU?"Ocultar":"Mostrar"}</button>
        { textU===true && <h2>{textI}</h2>}
        <input type="text" onChange={handleText}  />
    </div>   
}
