import { useState } from "react";

export const Counter = () => {

    const [counterA,setCounterA] = useState(0);
    const sumar = () =>{
        setCounterA(counterA+1);
    }

    const restar = () =>{
        setCounterA(counterA-1);        
    }

    return <div>
                <button onClick={restar}  >restar</button>
                <h1>{counterA}</h1>
                <button onClick={sumar}  >sumar</button>
            </div>
};
