import { useEffect, useState } from 'react';
import data from '../../datos.json';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar' 
import IconButton from '@mui/material/IconButton';
import SendIcon from '@mui/icons-material/Send';

export const ItemListContainer = () => {

    const [productos,setProductos] = useState([]);
    const getDatos = () =>{

        return new Promise((resolve,reject) =>{
                resolve(data)
        })
    }

    useEffect(() => {
    getDatos().then((res)=>{
        setProductos(res);
    });
    },[])

    let img = "https://images.unsplash.com/photo-1551963831-b3b1ca40c98e";

    console.log(productos.length);

    return <ImageList variant="masonry"  sx={{ width: '100%', height: '100%' }} cols={3} gap={8}>
        {productos.length > 0 && 

productos.map((producto) => {
    return (
         <ImageListItem key={producto.id}>
         <img
          srcSet={`${img}?w=148&h=148&fit=crop&auto=format&dpr=2 2x`}
          src={`${img}?w=148&h=148&fit=crop&auto=format`}
           alt={producto.first_name}
           loading="lazy"
         />

          <ImageListItemBar
              sx={{
                background:
                  'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, ' +
                  'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
              }}
              title={producto.first_name}
              position="top"
              actionIcon={
                <IconButton
                  sx={{ color: 'white' }}
                  aria-label={` ${producto.first_name}`}>
                  <SendIcon fontSize="inherit" />
                </IconButton>
              }
              actionPosition="Center"
            />



       </ImageListItem>


    )
})


