    useEffect(()=>{
        fetch(`https://pokeapi.co/api/v2/pokemon/${id}` )
    .then((resp) =>{
      //
        return resp.json();
    }).then((data) =>{
        setPokemon(data);
    });
    },[]
    );

    const Anterior = () =>{


         (id > 1 && setID(id -1));

    }

    const Siguiente = () =>{


        setID(id +1);

   }

    return (
        <div>
           {pokemon &&  
           <div>
                <h2>{pokemon.name}</h2>
